          </Modal.Header>
          <Modal.Body>
            OLA
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={closeModal}>Close</Button>
          </Modal.Footer>
        </Modal>
      );
  }

export default ModalComponent;
