import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import React, { useState, useEffect } from 'react';
import ModalSee from './modalSee';

function Athletetable() {

    const [data, setData] = useState([]);
    const [modalEdit, setEditModal] = useState({ show: false, id: null });
    const [modalSee, setSeeModal] = useState({ show: false, id: null });

    const handleClose = () => {
        setSeeModal({ show: false, data: null });
    };

    function getData() {

        let resp = fetch('http://192.168.160.58/Olympics/api/Games', {
          method: 'GET'
        }).then((dados)=>{
          dados.json().then((properties) => {

            var temp = [];
            properties["Records"].forEach((element) => {
              temp.push(<tr>
                            <td>{element["Name"]}</td>
                            <td>{element["CityName"]}</td>
                            <td>{element["CountryName"]}</td>
                            <td>{element["Year"]}</td>
                            <td align="center">
                                <Button variant="dark" size="sm" active onClick={() => setEditModal({ show: true, id: element["id"] })}>
                                    <i class="fas fa-edit"></i>
                                </Button>{' '}
                                <Button variant="success" size="sm" active onClick={() => setSeeModal({ show: true, id: element["id"] })}>
                                    <i class="fas fa-eye"></i>
                                </Button>
                            </td>
                        </tr>)
            });

            setData(temp);

          })
        }).catch((error) => {
          console.log("error")
        })
    }

  useEffect(() => {
    getData()
  }, [])

  return (
    <div>
        <Table striped bordered hover style={{width: '80%'}}>
            <thead style={{backgroundColor: 'grey', color: "white"}}>
                <tr>
                <th>Nome dos Jogos</th>
                <th>Cidade</th>
                <th>Pa√≠s</th>
                <th>Ano</th>
                <th></th>
                </tr>
            </thead>
            <tbody>
                {data}
            </tbody>
        </Table>
        {modalEdit.show && modalEdit.id && <ModalSee closeModal={handleClose} data={modalEdit.id} />}
        {modalSee.show && modalSee.id && <ModalSee closeModal={handleClose} data={modalSee.id} />}
    </div>
  );
}

export default Athletetable;
